class c{scrollToTopId="scroll-to-top";activeAttribute="data-active";isVisible=!1;lastScrollTop=0;initialize(){this.attachDocumentScroll(),this.attachScrollToTop()}attachScrollToTop(){document.getElementById(this.scrollToTopId)?.addEventListener("click",()=>{document.scrollingElement?.scrollTo({top:0,behavior:"smooth"})})}attachDocumentScroll(){document.addEventListener("scroll",()=>{this.onScroll()})}onScroll(){const{scrollingElement:t}=document;if(!t)return;const{clientHeight:o,scrollTop:l,scrollHeight:i}=t,e=o*.25;this.setVisibility(l<this.lastScrollTop&&l>e&&l+o<i-e),this.lastScrollTop=l}setVisibility(t){if(this.isVisible===t)return;this.isVisible=t,document.getElementById(this.scrollToTopId)?.setAttribute(this.activeAttribute,t?"true":"")}}const s=new c;s.initialize();
